import React, {useState, useEffect} from "react"

const img = [
    <img key={slide1} src={"https://pngicon.ru/file/uploads/2_16.png"} />,
    <img key={slide2} src={"https://pngicon.ru/file/uploads/2_16.png"} />,
    <img key={slide3} src={"https://pngicon.ru/file/uploads/2_16.png"} />,
    <img key={slide4} src={"https://pngicon.ru/file/uploads/2_16.png"} />,
    <img key={slide5} src={"https://pngicon.ru/file/uploads/2_16.png"} />,
]

export function Slider() {
    // Индекс текущего слайда
const [activeIndex, setActiveIndex] = useState(0);
 
// Хук Effect
useEffect(() => {
    // Запускаем интервал
    const interval = setInterval(() => {
        // Меняем состояние
        setActiveIndex((current) => {
            // Вычисляем индекс следующего слайда, который должен вывестись
            const res = current === img.length - 1 ? 0 : current + 1
            // Возвращаем индекс
            return res
        })
    }, 3000)
    // Выключаем интервал
    return () => clearInterval()
}, [])
 
// Вычисляем индекс предыдущего слайда
const prevImgIndex = activeIndex ? activeIndex - 1 : img.length - 1
// Вычисляем индекс следующего слайда
const nextImgIndex = activeIndex === img.length - 1 ? 0 : activeIndex + 1
}
