import  React from 'react';
import {BrowserRouter} from 'react-router-dom';
import "./css/main.css"

import './css/bootstrap.min.css'    
import './css/all.min.css'  
import './css/animate.css'  
import './css/magnific-popup.css'  
import './css/owl.min.css'
import './css/flaticon.css'  
import './css/odometer.css'

import  Carousel from "./pages/components/testComm/carousel"
import axios from 'axios';
/* import { SiteHeader } from './pages/components/header'; */
/* import { SiteFooter } from './pages/components/footer'; */


const Post_Component = ({posts }) =>{

  //const history = useHistory()
 // console.log("proverka0 ",posts)
  JSON.stringify(posts);
 // console.log("proverka1 ",posts )
  return(
      <div>
            {
                  posts.map((e,i)=>   <div style={{ maxWidth: 1200, marginLeft: 'auto', marginRight: 'auto', marginTop: 64 , float: "left"}}>
                  <div style={{background: "#EEDDCD"}}>
                          <div class="post-author">
                              <div class="thumb">
                                  <img src={e.avater?.url ? "https://cdn.lk-ft.ru"+e.avatar?.url : "https://pngicon.ru/file/uploads/2_16.png"} alt="blog"/>
                              </div>
                              <h6 class="title">{e?.lastname} {e?.firstname[0]}</h6>
                          </div>
                        </div>
                    </div> 
                      )
              }

      </div>
  )
}  

const Pagination = ({postPerPage, totalPost , paginate}) => {
  
  const pageNumbers = [];

  //console.log("leng" , totalPost)

  for(let i = 1; i <=Math.ceil(totalPost / postPerPage); i++){
      pageNumbers.push(i);
       
  }
  var i=0;
  if(pageNumbers.length == 1 ){
    return(
      <nav>
          <ul class="1 ">
            <a onClick={() => paginate(pageNumbers[0]--)}  > {"<"} </a>       
            <a onClick={() => paginate(pageNumbers[0]++)}  > {">"} </a>
          </ul>
      </nav>
  )
  }


  if(i==4){ console.log(i)
  }

  console.log(i);

  return(
      <nav>
          <ul class="1 ">
            <a onClick={() => paginate(pageNumbers[0]--)}  > {"<"} </a>       
            <a onClick={() => paginate(pageNumbers[0]++)}  > {">"} </a>
          </ul>
      </nav>
  )
}

function Pagination_Post(){

 
  const [currentPage, setCurrentPage] = React.useState(1);
  const [postPerPage] = React.useState(3);

  const [posts, setPosts] = React.useState([]);
  const [news, setNews] = React.useState([]);
const [isLoading, setIsLoading] = React.useState(false);
const [isError, setIsError] = React.useState(false);

React.useEffect(() => {

  const fetchStat = async () =>{
    setIsLoading(true);
    const res = await axios.get('https://cdn.lk-ft.ru/footballers');
    const res2 = await axios.get('https://cdn.lk-ft.ru/posts');
    setNews(res2.data);
    setPosts(res.data);
    setIsLoading(false);
  }
  fetchStat();
}, []); 

/* React.useEffect(()=>{
    const fetchPosts = async () =>{
        setIsLoading(true);
        const res = await axios.get('https://cdn.lk-ft.ru/posts');
        setPosts(res.data);
        setIsLoading(false);
    }
}) */
//get current post
const  indexOfLastPost = currentPage * postPerPage;
const  indexOfFirstPost = indexOfLastPost - postPerPage;
const  currentPosts = posts.slice(indexOfFirstPost,indexOfLastPost);
const  currentNews = news.slice(indexOfFirstPost,indexOfLastPost);



  


// Change Page
const paginate = (pageNumber) => setCurrentPage(pageNumber);

      return(
          <>
             
             <Post_Component posts={currentPosts}  />
             <News_Component news={currentNews}/>
             <Pagination postPerPage={postPerPage} totalPost={posts.length} paginate={paginate} />

      </>
      )
  
} 


const News_Component = ({news }) =>{

  //const history = useHistory()
 // console.log("proverka0 ",posts)
  JSON.stringify(news);
 // console.log("proverka1 ",posts )
  return(
      <div>
            {
                  news.map((e,i)=>   <div style={{ maxWidth: 1200, marginLeft: 'auto', marginRight: 'auto', marginTop: 64 , float: "left"}}>
                  <div style={{background: "#EEDDCD"}}>
                          <div class="post-author">
                              <div class="thumb">
                                  <img src={e.Post_image?.url ? "https://cdn.lk-ft.ru"+e.Post_image?.url : "https://pngicon.ru/file/uploads/2_16.png"} alt="blog"/>
                              </div>
                              <h6 class="title">{e?.Post_author} {e?.Post_author_lastname[0]}</h6>
                          </div>
                        </div>
                    </div> 
                      )
              }

      </div>
  )
}  



function App() {
 
  
  




      return(
          <>
             
             <Pagination_Post  />
      </>
      )
  
}



