import React,{useState} from "react";

export const Events = () =>{
    const [show,setShow] = useState(false);

    if(!show){
        return(
            <section>
                <div class="container events" onClick={()=>setShow(true)}>
            
                    <h1 class="title" style={{cursor:'pointer'}} >Мероприятия </h1>
                    <h1>&#8744;</h1>
            
                </div>
            </section>
        )
    }else{
        return(
            <section>
                <div class="container">
            
                   <div class="container events" onClick={()=>setShow(false)}>
            
                        <h1 class="title" style={{cursor:'pointer'}} >Мероприятия </h1>
                        <h1>&#8743;</h1>
                
                    </div>
                    <div class='inside-container'>
                       <ul>
                           <li> Соревнования 2 VS 2:</li>
                           <li> Соревнования 3 VS 3:</li>
                           <li> Пенальти:</li>
                       </ul>
                    </div>
                </div>
            </section>
        )
    }

}
