    const location = useLocation()
    const isLogin = location.pathname ===  AUTH_ROUTE

    var email_user;
    var pass_user;

      const [player, setPlayer] = React.useState([]);
      const fetchDataPlayer = () => {
          fetch('https://cdn.lk-ft.ru/footballers')
          .then((response) => response.json())
          .then((data) => {
              setPlayer(data);
          })
      };
      React.useEffect(() => {
          fetchDataPlayer();
      }, []);
  
      

