import axios from "axios";
import React, { Fragment } from "react"
import { useParams } from "react-router";
/* 
import {observer} from "mobx-react-lite";
 */


export const SelectionShedule = () =>{
      const params = useParams()
      const it_number = params.id;

      var lastPay;
      var countLessons;

      const [shedule, setShedule] = React.useState([]);
      const [payDay, setPayDay] = React.useState([]);
      const [loading,setLoading] = React.useState([]) 
      React.useEffect(() => {  
          const fetchStat = async () =>{
            setLoading(true);
            const res = await axios.get('https://cdn.lk-ft.ru/scheduleas');
            const payDay = await axios.get('https://cdn.lk-ft.ru/footballers')
            setShedule(res.data)
            setPayDay(payDay.data)
            setLoading(false);
          }
    
          fetchStat();
      }, []);

      console.log(payDay);
      payDay.map((e,i) => {if(it_number == e.id) console.log(it_number , e.id)})
    return(
        <Fragment>
                <section class="history-section padding-top-2 padding-bottom-2" id="history">
         <div class="container">
             <div  class="section-header-2">
                 <h2 class="title">
                     <span class="text-theme">Расписание Занятий</span>
                 </h2>
             </div>
             <div class="history-wrapper" style={{display: "flex" }}>
            {shedule.map((e,i) => <>
                
                 <div class="history-item-2">
                     <div class="history-content">
                         <h3 class="title">{e.name}</h3>
                         <ul class="common-list">
                             <li>
                                 Понедельник: {e.MondayStart}
                             </li>
                             <li>
                                 Вторник: {e.ThursdayStart}
                             </li>
                             <li>
                                 Среда: {e.WednesdayStart}
                             </li>
                             <li>
                                 Четверг: {e.ThursdayStart}
                             </li>
                             <li>
                                 Пятница: {e.FridayStart}
                             </li>
                             <li>
                                 Суббота: {e.SaturdayStart}
                             </li>
                             <li>
                                 Воскресенье: {e.SundayStart}
                             </li>
                         </ul>
                     </div>
                 </div>
             
            </>)}</div>
         </div>
     </section>
        </Fragment>
    )
}
