import  React from "react";
import { useParams } from "react-router";

import { BannerPlayer } from "./components/bannerPlayer";
import { SiteHeader } from "./components/header";
import { SiteFooter } from "./components/footer";
/* import { SectionAbout } from "./components/sectionAbout";
import { SectionAwards } from "./components/sectionAwards"; */
import { SectionBlog } from "./components/sectionBlog";
/* import { SectionBlog } from "./components/sectionBlog"; */
/* import { SectionGallery } from "./components/sectionGallery"; */
/* import { SectionHistory } from "./components/sectionHistory"; */
/* import { SectionMoments } from "./components/sectionMoments"; */
/* import { SectionRecords } from "./components/sectionRecords"; */
import { Stats } from "./Stats";
import { Chart } from "react-google-charts";
import { useStatistics } from './components/hooks/useStat';



export const Home = () => {
  var stats=[];
  const { get, set } = useStatistics();

  const params = useParams()
  const it_number = params.id;
  
  const [player, setPlayer] = React.useState([]);
  const [isLoading, setIsLoading] = React.useState(false);
  const [isError, setIsError] = React.useState(false);
  const fetchDataPlayer = () => {
      fetch('https://cdn.lk-ft.ru/footballers')
      .then((response) => response.json())
      .then((data) => {
          setIsLoading(false);
          setPlayer(data);
      }) .catch((error) => {
        setIsLoading(false);
        setIsError(true);
        console.log(error);
    });
  };
  React.useEffect(() => {
      fetchDataPlayer();
  }, []);
  if (player== null) {
    console.log("null")
    }

  var firstname,lastname,position;
  
  player.map((e,i) => {if(e.id == it_number){firstname=e.firstname ; lastname = e.lastname ; position = e.playerPosition ; stats = e.Statistics}})

  let i=0;
  do{
    i++;
    if (i == 100 || stats.length != 0 ) break; 
    console.log(stats)
  }while(stats.length == 0)
  

  
    return(
      <>
      <SiteHeader />
          <body> 
            <BannerPlayer firstname={firstname} lastname={lastname} position={position} />
           {/*  <SectionAbout /> 
            <SectionHistory />  */}
            <section className="section-header-2" style={{marginBottom: "-20px"}}><ul>
          <div  class="container ">
                 <h2 class="title">
                     <a name="myhistory" href="#0"><div class="stroke-text">Статистика</div></a>
                     <span class="text-theme">Моя</span> <span>Статистика</span>
                 </h2>
             </div>
        </ul>
         <ul  style={{
          width:"100%",
          display: "flex",
          justifyContent: "space-around",
          alignItems: "center",
          flexWrap: "wrap"
        }}><div className="title-chart">
            <span class="text-theme"style={{
    position: "absolute",
    marginTop: "-4px"}}>Удар</span> 
          <Chart className="mt-4"
       width={"500px"}
       height={"350px"}
       chartType="LineChart"
       data={[
        [{ type: 'date',label:"Дата", id: 'Date' }, { type: 'number',label:"Удар", id: 'Won/Loss' }],
      //   ...get()?.ReactionList
      ].concat(get()?.HitList || [])}
        options={{
            chart: {
                subtitle: 'Оценка'
              },
        }}
        rootProps={{ 'data-testid': '2' }}
      />
        </div>
        <div className="title-chart" >
      <span class="text-theme" style={{
    position: "absolute",
    marginTop: "-4px"}}>Скорость</span>
      <Chart className="mt-4"
        width={"500px"}
        height={"350px"}
        chartType="LineChart"
        data={[
          [{ type: 'date',label:"Дата", id: 'Date' }, { type: 'number', label:"Скорость", id: 'Won/Loss' }],
        //   ...get()?.ReactionList
        ].concat(get()?.SpeedList || [])}
        options={{
            chart: {
                
                subtitle: 'Оценка',
                chartArea: { width: '50%' },
                colors: ['#ffab91'],

              },
        }}
        rootProps={{ 'data-testid': '2' }}
      /></div>
      <div className="title-chart" >
      <span class="text-theme" style={{
    position: "absolute",
    marginTop: "-4px"}}>Прыжок</span>
      <Chart className="mt-4"
        width={"500px"}
        height={"350px"}
        chartType="LineChart"
        data={[
          [{ type: 'date',label:"Дата", id: 'Date' }, { type: 'number',label:"Прыжок", id: 'Won/Loss' }],
        //   ...get()?.ReactionList
        ].concat(get()?.JumpList || [])}
        options={{
            chart: {
                
                subtitle: 'Оценка',
                chartArea: { width: '50%' }
              },
        }}
        rootProps={{ 'data-testid': '2' }}
      /></div>
      <div className="title-chart" >
      <span class="text-theme" style={{
        position: "absolute",
        marginTop: "-4px"}}>Реакция</span>
      <Chart className="mt-4"
       width={"500px"}
       height={"350px"}
       chartType="LineChart"
       data={[
        [{ type: 'date',label:"Дата", id: 'Date' }, { type: 'number',label:"Реакция", id: 'Won/Loss' }],
      //   ...get()?.ReactionList
      ].concat(get()?.ReactionList || [])}
        options={{
            chart: {
               
                subtitle: 'Оценка',
                chartArea: { width: '50%' }
              },
        }}
        rootProps={{ 'data-testid': '2' }}
      /></div></ul>
        </section>
           {/*  <SectionRecords /> 
            <SectionAwards />  */}
         {/*    <SectionGallery /> */}
           {/*  <SectionSkills /> */}
            {/* <SectionMoments /> */}
           {/*  <SectionClient />  */}
             <SectionBlog /> 
    </body>
    <SiteFooter />
    </>
    )
}
