import React, { Component } from 'react'
import { Button, Card, Container, Form, Row } from 'react-bootstrap'
import { NavLink /* , useLocation */ } from 'react-router-dom'
import { AUTH_ROUTE/* , REGISTRATION_ROUTE */ } from './components/utils/consts'


//const location = useLocation()
 //   const isLogin = location.pathname ===  AUTH_ROUTE


 function  GetEmailPass() {
    const [player, setPlayer] = React.useState([]);
    const fetchDataPlayer = () => {
        fetch('https://cdn.lk-ft.ru/footballers')
        .then((response) => response.json())
        .then((data) => {
            setPlayer(data);
        })
    };
    React.useEffect(() => {
        fetchDataPlayer();
    }, []);
    
    return(player);
}

class Auth extends Component {
    state = {
        val: "" ,
        pass: ""
      };
      onSubmit = () => {
        console.log("email" ,this.state.val);
        console.log("pass ", this.state.pass);
      };
      render(){

        var player = GetEmailPass()
        
        console.log(player);

        var pass_enter = this.state.pass;
        var email_enter = this.state.val;
          
    return (
        <Container 
        className="d-flex justify-content-center aling-items-center"
        style={{height: window.innerHeight-54 , marginTop:window.innerHeight-700}}
        >
            <Card style={{width: 600}} className="p-5">
                
                <Form className="d-flex flex-column">
                <h2 className="m-auto" style={{color:'black'}}>{/* isLogin? */ 'Авторизация' /* : 'Регистрация' */}</h2>
                    <Form.Control 
                        className="mt-3"
                        placeholder="Введите ваш email..."
                        type="email"
                        value={this.state.val}
                        onChange={e => this.setState({ val: e.target.value })}
                    />
                    <Form.Control 
                        className="mt-3"
                        placeholder="Введите ваш пароль..."
                        type="password"
                        value={this.state.pass}
                        onChange={e => this.setState({ pass: e.target.value })}
                        
                    />
                    <Row className="d-flex justify-content-between mt-3 pl-3 pr-3">
                       {/*  {isLogin ? */}
                        {/* <div>
                            Нет аккаунта? <NavLink to={REGISTRATION_ROUTE}> Зарегистрируйся! </NavLink>
                        </div>
                        : */}
                        <div>
                            Есть аккаунт? <NavLink to={AUTH_ROUTE}> Войдите! </NavLink>
                        </div>
                        {/* } */}
                        <Button 
                            variant={"outline-success"}
                            onClick={this.onSubmit}>
                               {/* isLogin ? */ 'Войти '/*  : 'Регистрация' */} 
                        </Button>
                    </Row>    
                </Form>
            </Card>

        </Container>
    );
                    }
}


export {Auth};
