import  React from "react";
import { useParams } from "react-router";

import { BannerPlayer } from "./components/bannerPlayer";
import { SiteHeader } from "./components/header";
import { SiteFooter } from "./components/footer";
/* import { SectionAbout } from "./components/sectionAbout";
import { SectionAwards } from "./components/sectionAwards"; */
import { SectionBlog } from "./components/sectionBlog";
/* import { SectionBlog } from "./components/sectionBlog"; */
/* import { SectionGallery } from "./components/sectionGallery"; */
/* import { SectionHistory } from "./components/sectionHistory"; */
/* import { SectionMoments } from "./components/sectionMoments"; */
/* import { SectionRecords } from "./components/sectionRecords"; */
import { Stats } from "./Stats";
import axios from "axios";
import { SelectionShedule } from "./components/shedule";
import { MiddleStats } from "./MidleStats";




export const Home = () => {

  const params = useParams()
  const it_number = params.id;

  const [player, setPlayer] = React.useState([]);
/*   const fetchDataPlayer = () => {
      fetch('https://cdn.lk-ft.ru/footballers')
      .then((response) => response.json())
      .then((data) => {
          setPlayer(data);
      })
  }; */

  // const [stats , setStats] = React.useState([])
  const [loading,setLoading] = React.useState([]) 
  React.useEffect(() => {
     // fetchDataPlayer();

      const fetchStat = async () =>{
        setLoading(true);
        const res = await axios.get('https://cdn.lk-ft.ru/footballers');
        setPlayer(res.data)
        setLoading(false);
      }

      fetchStat();
  }, []);
//  console.log(loading)
 /*  console.log("rabotaem",star) */

  var firstname,lastname,position, avatar, birthday,lead_leg,team ;
  
  var star= [];

  player.map((e,i) => {if(e.id == it_number){firstname=e.firstname ; lastname = e.lastname ; position = e.playerPosition ; avatar=e.avatar?.url; star = e.Statistics ;birthday=e.birthday;lead_leg = e.lead_leg;team=e.team ;console.log(e) }})
  
/*  var full_stats = []; player.map((e,i) =>{ full_stats.push(e.id) ; full_stats.push(e.Statistics)}); */

  if(avatar!=null){
    avatar= "https://cdn.lk-ft.ru"+avatar;
  }else{
    avatar="/images/banner/head.png";
  }
  
  console.log(star)
    return(
      <>
      
      <SiteHeader />
          <body>
             
            <BannerPlayer firstname={firstname} lastname={lastname} position={position} avatar={avatar} birthday={birthday} lead_leg={lead_leg} team={team}/>
           {/*  <SectionAbout /> 
            <SectionHistory />  */}
            <Stats stats={star}/> 
            <MiddleStats stats={star} />
           {/*  <SectionRecords /> 
            <SectionAwards />  */}
         {/*    <SectionGallery /> */}
           {/*  <SectionSkills /> */}
            {/* <SectionMoments /> */}
             <SelectionShedule />
             <SectionBlog />
             <SiteFooter /> 
    </body>
    </>
    )
}
