import React,{useState} from "react";

export const FullInformation = () =>{
    const [show,setShow] = useState(false);

    if(!show){
        return(
       
                <div class="container">
            
                    <h6 class="title" style={{cursor:'pointer',color:'#03fc41'}} onClick={()=>setShow(true)}>Подробнее про систему Достиженй:</h6>
            
                </div>
        )
    }else{
        return(
           
                <div class="container">
            
                    <h6 class="title" style={{cursor:'pointer',color:'#039928'}} onClick={()=>setShow(false)}>Подробнее про систему Достиженй:</h6>
                    <div>
                        Полный подсчет всех пунктов
                    </div>
            
                </div>
        )
    }
    
}
