const Post_Component = ({posts }) =>{

  //const history = useHistory()
 // console.log("proverka0 ",posts)
  JSON.stringify(posts);
 // console.log("proverka1 ",posts )
  return(
      <div>
            {
                  posts.map((e,i)=>   <div style={{ maxWidth: 1200, marginLeft: 'auto', marginRight: 'auto', marginTop: 64 , float: "left"}}>
                  <div style={{background: "#EEDDCD"}}>
                          <div class="post-author">
                              <div class="thumb">
                                  <img src={e.avater?.url ? "https://cdn.lk-ft.ru"+e.avatar?.url : "https://pngicon.ru/file/uploads/2_16.png"} alt="blog"/>
                              </div>
                              <h6 class="title">{e?.lastname} {e?.firstname[0]}</h6>
                          </div>
                        
                        </div>
                    </div> 
                      )
              }

      </div>
  )
}  

const Pagination = ({postPerPage, totalPost , paginate}) => {
  
  const pageNumbers = [];

  //console.log("leng" , totalPost)

  for(let i = 1; i <=Math.ceil(totalPost / postPerPage); i++){
      pageNumbers.push(i);
       
  }
  var i=0;
  if(pageNumbers.length == 1 ){
    return(
      <nav>
          <ul class="1 ">
            <a onClick={() => paginate(pageNumbers[0]--)} href="#about" > {"<"} </a>       
            <a onClick={() => paginate(pageNumbers[0]++)} href="#about" > {">"} </a>
          </ul>
      </nav>
  )
  }


  if(i==4){ console.log(i)
  }

  console.log(i);

  return(
      <nav>
          <ul class="1 ">
            <a onClick={() => paginate(pageNumbers[0]--)} href="#about" > {"<"} </a>       
            <a onClick={() => paginate(pageNumbers[0]++)} href="#about" > {">"} </a>
          </ul>
      </nav>
  )
}

function Pagination_Post(){

 
  const [currentPage, setCurrentPage] = React.useState(1);
  const [postPerPage] = React.useState(3);

  const [posts, setPosts] = React.useState([]);
const [isLoading, setIsLoading] = React.useState(false);
const [isError, setIsError] = React.useState(false);
 const fetchData = () => {
  fetch('https://cdn.lk-ft.ru/footballers')
    .then((response) => response.json())
    .then((data) => {
      setIsLoading(false);
      setPosts(data);
    })
    .catch((error) => {
      setIsLoading(false);
      setIsError(true);
      console.log(error);
    });
};
React.useEffect(() => {
  fetchData();
}, []); 

