import  React from "react";
import { useParams } from "react-router";

import { BannerPlayer } from "./components/bannerPlayer";
import { SiteHeader } from "./components/header";
import { SiteFooter } from "./components/footer";
/* import { SectionAbout } from "./components/sectionAbout";
import { SectionAwards } from "./components/sectionAwards"; */
import { SectionBlog } from "./components/sectionBlog";
/* import { SectionBlog } from "./components/sectionBlog"; */
/* import { SectionGallery } from "./components/sectionGallery"; */
/* import { SectionHistory } from "./components/sectionHistory"; */
/* import { SectionMoments } from "./components/sectionMoments"; */
/* import { SectionRecords } from "./components/sectionRecords"; */
import { Stats } from "./Stats";
import axios from "axios";




export const Home = () => {

  const params = useParams()
  const it_number = params.id;

  const [player, setPlayer] = React.useState([]);
/*   const fetchDataPlayer = () => {
      fetch('https://cdn.lk-ft.ru/footballers')
      .then((response) => response.json())
      .then((data) => {
          setPlayer(data);
      })
  }; */

  // const [stats , setStats] = React.useState([])
  const [loading,setLoading] = React.useState([]) 
  React.useEffect(() => {
     // fetchDataPlayer();

      const fetchStat = async () =>{
        setLoading(true);
        const res = await axios.get('https://cdn.lk-ft.ru/footballers');
        setPlayer(res.data)
        setLoading(false);
      }

      fetchStat();
  }, []);
//  console.log(loading)
  //console.log("rabotaem" ,stats)

  var firstname,lastname,position, avatar;
  
  var star = [];
  player.map((e,i) => {if(e.id == it_number){firstname=e.firstname ; lastname = e.lastname ; position = e.playerPosition ; avatar=e.avatar.url; star = e.Statistics }})
  if(avatar!=null){
    avatar= "https://cdn.lk-ft.ru"+avatar;
  }else{
    avatar="https://cdn.lk-ft.ru/uploads/png_transparent_cartoon_drawing_whatsapp_boys_comics_face_head_653814e7e6.png";
  }
  
  //console.log(star)
    return(
      <>
      
      <SiteHeader />
          <body>
             
            <BannerPlayer firstname={firstname} lastname={lastname} position={position} avatar={avatar} />
           {/*  <SectionAbout /> 
            <SectionHistory />  */}
            <Stats stats={star}/> 
           {/*  <SectionRecords /> 
            <SectionAwards />  */}
         {/*    <SectionGallery /> */}
           {/*  <SectionSkills /> */}
            {/* <SectionMoments /> */}
           {/*  <SectionClient />  */}
             <SectionBlog />
             <SiteFooter /> 
    </body>
    </>
    )
}
