import React, { Component} from "react";
import PropTypes from 'prop-types'
import className from 'classname'


 export default class Navbar extends Component {
    static propTypes = {
        children: PropTypes.node.isRequired,
        content: PropTypes.string, 
    }

    static defaultProp ={
        content: 'Tooltip content',
        style:{},
    }

    state ={
        visible: false,
    }

    show = () =>{
        this.setVisibility(true);
    }

    hide = () =>{
        this.setVisibility(false);
    }

    setVisibility = visible =>{
        this.setState({visible});
    }

    render(){

        const {visible}= this.state;

        const {children, content, style} = this.props;

        const classes = className(
            'submenu'
        );
    

        return(
            <header className="header-section">
                <div className="container">
                    <div className="header-wrapper">
                        <div className="logo">
                            <a href="index.html"><img src={logo} alt="logo"/></a>
                        </div>
                        <ul className="menu ml-lg-auto">
                            <li>
                                <a href="#0"  
                                    onMouseEnter = {this.show } 
                                    onMouseLeave={this.hide}
                                >Home</a>
                                    {visible && <ul className={classes}>
                                        <li>
                                            <a href="/">Home 1</a>
                                        </li>
                                        <li>
                                            <a href="/second">Home 2</a>
                                        </li>
                                    </ul>}
                            </li>
                            <li>
                                <a href="#0" 
                                        onMouseEnter = {this.show } 
                                        onMouseLeave={this.hide}
                                >Blog</a>
                                {visible && <ul className={classes}>
                                    <li>
                                        <a href="blog.html">Blog</a>
                                    </li>
                                    <li>
                                        <a href="blog-single.html">Blog Single</a>
                                    </li>
                                </ul>}
                            </li>
                            <li>
                                <a href="award.html">awards</a>
                            </li>
                            <li>
                                <a href="contact.html">contact</a>7
                            </li>
                            <li>
                                <a href="#0" className="custom-button">Follow Me</a>
                            </li>
                        </ul>
                        <div className="header-bar">
                            <span></span>
                            <span></span>
                            <span></span>
                        </div>
                    </div>
                </div>
            </header>
        );

    }
}
