import  React from "react";
import { useParams } from "react-router";

import { BannerPlayer } from "./components/bannerPlayer";
import { SiteHeader } from "./components/header";
import { SiteFooter } from "./components/footer";
/* import { SectionAbout } from "./components/sectionAbout";
import { SectionAwards } from "./components/sectionAwards"; */
import { SectionBlog } from "./components/sectionBlog";
/* import { SectionBlog } from "./components/sectionBlog"; */
/* import { SectionGallery } from "./components/sectionGallery"; */
/* import { SectionHistory } from "./components/sectionHistory"; */
/* import { SectionMoments } from "./components/sectionMoments"; */
/* import { SectionRecords } from "./components/sectionRecords"; */
import { Stats } from "./Stats";

import { useStatistics } from './components/hooks/useStat';

import { observe } from 'mobx';
import { Carousel } from "react-bootstrap";

export const Home = () => {

  const { get, set } = useStatistics();

  const params = useParams()
  const it_number = params.id;
  
  const [player, setPlayer] = React.useState([]);
  const [isLoading, setIsLoading] = React.useState(false);
  const [isError, setIsError] = React.useState(false);
  const fetchDataPlayer = () => {
      fetch('https://cdn.lk-ft.ru/footballers')
      .then((response) => response.json())
      .then((data) => {
          setIsLoading(false);
          setPlayer(data);
      }) .catch((error) => {
        setIsLoading(false);
        setIsError(true);
        console.log(error);
    });
  };
  React.useEffect(() => {
      fetchDataPlayer();
  }, []);
  if (player== null) {
    console.log("null")
    }

  var firstname,lastname,position;
  var stats=[];
  player.map((e,i) => {if(e.id == it_number){firstname=e.firstname ; lastname = e.lastname ; position = e.playerPosition ; stats = e.Statistics} console.log(e.firstname[0] , 
        e.lastname , e.avatar , e.Statistics)})

    
    return(
      <>

           <Widget />


    </>
    )
}







export const Widget = () => {
 
  return (
    <Carousel variant="dark">
  <Carousel.Item>
    <img
      className="d-block w-100"
      src="holder.js/800x400?text=First slide&bg=f5f5f5"
      alt="First slide"
    />
    <Carousel.Caption>
      <h5>First slide label</h5>
      <p>Nulla vitae elit libero, a pharetra augue mollis interdum.</p>
    </Carousel.Caption>
  </Carousel.Item>
  <Carousel.Item>
    <img
      className="d-block w-100"
      src="holder.js/800x400?text=Second slide&bg=eee"
      alt="Second slide"
    />
    <Carousel.Caption>
      <h5>Second slide label</h5>
      <p>Lorem ipsum dolor sit amet, consectetur adipiscing elit.</p>
    </Carousel.Caption>
  </Carousel.Item>
  <Carousel.Item>
    <img
      className="d-block w-100"
      src="holder.js/800x400?text=Third slide&bg=e5e5e5"
      alt="Third slide"
    />
    <Carousel.Caption>
      <h5>Third slide label</h5>
      <p>Praesent commodo cursus magna, vel scelerisque nisl consectetur.</p>
    </Carousel.Caption>
  </Carousel.Item>
</Carousel>
  )
}
