import  React, { useContext, useState } from "react";
import { useHistory } from "react-router-dom";
import { Context } from "..";
import { SiteFooter } from "./components/footer";
import { SiteHeader } from "./components/header";
import { BLOG_SINGLE_ROUTE } from "./components/utils/consts";
import list_posts from "./post.json";
import {observer} from "mobx-react-lite";
import { Row } from "react-bootstrap";
import { blog_single } from "./blog-single";

const PostList = observer(()=>{
    const {posts} = useContext(Context)
    return(
        
        <Row className="d-flex">
            <section class="hero-section">
                <a href="#about" class="banner-icon">
                    <i class="flaticon-down-arrow"></i>
                </a>
                <div class="container">
                    <div class="hero-content">
                        <h1 class="title" data-bg="БЛОГ"><span>Последние Новости</span></h1>
                        <ul class="breadcrumb">
                            <li>
                                <a href="/">Домашняя Страница</a>
                            </li>
                            <li>
                                <span>Блог</span>
                            </li>
                        </ul>
                    </div>
                </div>
            </section>

            <section class="blog-section padding-top padding-bottom" id="about">
                <div class="container">
                    <div class="row justify-content-center mb-40-60-none">
                    {posts.posts.map(posts => 
                        <Pagination_Post key={posts.id} blog_post={posts} />
                    )}
                    
                  </div>
                </div>
            </section>
        </Row>
    );
});

 



const Post_Component = ({posts, blog_post }) =>{

    const history = useHistory()

    console.log(history)
    return(
        <>
              {
                <div  class="col-lg-10">
                    <div class="post-item">
                        <div class="post-thumb">
                            <a href="blog-single.html">
                                <img src={blog_post.img} alt="blog"/>
                            </a>
                        </div>
                        <div class="post-content">
                            <div class="post-date">
                                <a href="#0"><i class="flaticon-clock"></i>{blog_post.publishedAt}</a>
                            </div>
                            <ul class="post-tags">
                                <li>
                                    <a href="#0">Видео</a>
                                </li>
                                <li>
                                    <a href="#0">Invest Ment</a>
                                </li>
                                <li>
                                    <a href="#0">Фильм</a>
                                </li>
                            </ul>
                            <h4 class="title">
                                <a href="/blog_single">{blog_post.title}</a>
                            </h4>
                            <p>{blog_post.description}</p>
                            <a href="#0" class="custom-button" onClick={()=> history.push(BLOG_SINGLE_ROUTE+'/'+blog_post.id)} >Подробности</a>
                        </div>
                        
                    </div>
                </div>
                        
                }

        </>
    )
}  

const Pagination = ({postPerPage, totalPost , paginate}) => {
    
    const pageNumbers = [];


    for(let i = 1; i <=Math.ceil(totalPost / postPerPage); i++){
        pageNumbers.push(i);
         
    }

    return(
        <nav>
            <ul class="pagination mt-4">
           
                {pageNumbers.map( number =>
                     
                 <li key={number}  >
                     <a onClick={() => paginate(number)} href="#0" >{number}</a>
                 </li>
                 
                 )}
               
            </ul>
        </nav>
    )
}

 function Pagination_Post({blog_post}){

    const [currentPage, setCurrentPage] = useState(1);
    const [postPerPage] = useState(3);

//get current post
  const  indexOfLastPost = currentPage * postPerPage;
  const  indexOfFirstPost = indexOfLastPost - postPerPage;
  const currentPost = list_posts.posts.slice( indexOfFirstPost , indexOfLastPost);  
 

// Change Page
  const paginate = (pageNumber) => setCurrentPage(pageNumber);

        return(
            <>
               
               <Post_Component posts={currentPost} blog_post={blog_post}  />
               <Pagination postPerPage={postPerPage} totalPost={list_posts.posts.length} paginate={paginate} />

        </>
        )
    
  }
