import  React from 'react';
import {BrowserRouter} from 'react-router-dom';
import "./css/main.css"

import './css/bootstrap.min.css'    
import './css/all.min.css'  
import './css/animate.css'  
import './css/magnific-popup.css'  
import './css/owl.min.css'
import './css/flaticon.css'  
import './css/odometer.css'

import  Carousel from "./pages/components/testComm/carousel"
/* import { SiteHeader } from './pages/components/header'; */
/* import { SiteFooter } from './pages/components/footer'; */


function App() {

  const [player, setPlayer] = React.useState([]);
  const [isLoading, setIsLoading] = React.useState(false);
  const [isError, setIsError] = React.useState(false);
  const fetchDataPlayer = () => {
      fetch('https://cdn.lk-ft.ru/footballers')
      .then((response) => response.json())
      .then((data) => {
          setIsLoading(false);
          setPlayer(data);
      }) .catch((error) => {
        setIsLoading(false);
        setIsError(true);
        console.log(error);
    });
  };
  React.useEffect(() => {
      fetchDataPlayer();
  }, []);
  if (player== null) {
    console.log("null")
    }


  return (

        <div style={{ maxWidth: 1200, marginLeft: 'auto', marginRight: 'auto', marginTop: 64 , float: "left"}}>
            <Carousel> 
              {player.map((e,i)=><div style={{background: "#EEDDCD"}}>
                    <div class="post-author">
                        <div class="thumb">
                            <img src={e.avater?.url ? "https://cdn.lk-ft.ru"+e.avatar?.url :"https://pngicon.ru/file/uploads/2_16.png"} alt="blog"/>
                        </div>
                        <h6 class="title">{e.lastname} {e.firstname[0]}</h6>
                    </div>
                </div>)}
            </Carousel>
        </div>
  );
}

export default App;
