import { formatRFC3339 } from "date-fns";
import React, { Fragment, useState } from "react";
import { SiteFooter } from "./components/footer";
import { SiteHeader } from "./components/header";





export const award = () =>{
    return(
        <Fragment>
            <SiteHeader />

    <section class="hero-section">
        <a href="#about" class="banner-icon">
            <i class="flaticon-down-arrow"></i>
        </a>
        <div class="container">
            <div class="hero-content">
                <h1 class="title" data-bg="Награды"><span>Награды</span></h1>
                <ul class="breadcrumb">
                    <li>
                        <a href="/home">Домашняя Страница</a>
                    </li>
                    <li>
                        <span>Награды</span>
                    </li>
                </ul>
            </div>
        </div>
    </section>

    <section class="record-section padding-top padding-bottom" id="about">
        <div class="container">
            <div class="mb-30-none">

                <Slider_Award />

            </div>
            <ul class="pagination">
                <li class="arrows disabled">
                    <span><i class="flaticon-angle-pointing-to-left"></i></span>
                </li>
                <li>
                    <a href="#0">01</a>
                </li>
                <li>
                    <span>02</span>
                </li>
                <li>
                    <a href="#0">03</a>
                </li>
                <li class="arrows">
                    <a href="#0"><i class="flaticon-angle-arrow-pointing-to-right"></i></a>
                </li>
            </ul>
        </div>
    </section>

    <SiteFooter />
        </Fragment>
    )
}

 export default function Slider_Award(){
    const wordsArray = ["Название Награды 1", "Название Награды 2","Награда 3", "Награда 4" , "Награда 5"];
    const imgArray= ["./images/award/01.png","./images/award/05.png","./images/award/03.png", "./images/award/04.png" , "./images/award/05.png"];
    const textArray=["Большой Текст 1" , "Большой Текст 2", "Большой Текст 3","Большой Текст 4", "Большой Текст 5", ];
    const countArray=[1,22,44,55,66];


    const wordsArray2 = ["Название Награды 6", "Название Награды 7","Награда 8", "Награда 9" , "Награда 10"];
    const imgArray2= ["./images/award/01.png","./images/award/05.png","./images/award/03.png", "./images/award/04.png" , "./images/award/05.png"];
    const textArray2=["Большой Текст 6" , "Большой Текст 7", "Большой Текст 8","Большой Текст 9", "Большой Текст 10", ];
    const countArray2=[1,22,44,55,66];


    const wordsArray3 = ["Название Награды 11", "Название Награды 12","Награда 13", "Награда 14" , "Награда 15"];
    const imgArray3= ["./images/award/01.png","./images/award/05.png","./images/award/03.png", "./images/award/04.png" , "./images/award/05.png"];
    const textArray3=["Большой Текст 11" , "Большой Текст 12", "Большой Текст 13","Большой Текст 14", "Большой Текст 15", ];
    const countArray3=[15,14,13,12,11];

    const [showMode, setShowMode] = useState(true);
    const [showMode2, setShowMode2] = useState(false);
    const [showMode3, setShowMode3] = useState(false);


    const handlerClicker = () =>{
        setShowMode(!showMode);
    };

    const handlerClicker2 = () =>{
        setShowMode2(!showMode2);
    };

    const handlerClicker3 = () =>{
        setShowMode3(!showMode3);
    };

    let content = null;
    let content2 = null;
    let content3 = null;

    if(showMode){
        content = (
            <>
            {imgArray.map((e,i)=><div class="award-item">
                        <div class="award-thumb">
                            <div class="thumb">
                               <img src={imgArray[i]} alt="award"/>
                            </div>
                            <div class="cont">
                                <span>x</span>{countArray[i]}
                            </div>
                        </div>
                        <div class="award-content">
                            <h4 class="title">{wordsArray[i]}</h4>
                            <p>{textArray[i]}</p>
                        </div>
                    </div>)}
                
            </>
        )
    }

    if(showMode2){
        content2 = (
            <>
            {imgArray.map((e,i)=><div class="award-item">
                        <div class="award-thumb">
                            <div class="thumb">
                               <img src={imgArray2[i]} alt="award"/>
                            </div>
                            <div class="cont">
                                <span>x</span>{countArray2[i]}
                            </div>
                        </div>
