            <a onClick={() => paginate(pageNumbers[i]++)} href="#about" > {">"} </a>
          </ul>
      </nav>
  )
}

function Pagination_Post(){


  const [currentPage, setCurrentPage] = React.useState(1);
  const [postPerPage] = React.useState(1);

  const [posts, setPosts] = React.useState([]);
const [isLoading, setIsLoading] = React.useState(false);
const [isError, setIsError] = React.useState(false);
 const fetchData = () => {
  fetch('https://cdn.lk-ft.ru/footballers')
    .then((response) => response.json())
    .then((data) => {
      setIsLoading(false);
      setPosts(data);
    })
    .catch((error) => {
      setIsLoading(false);
      setIsError(true);
      console.log(error);
    });
};
React.useEffect(() => {
  fetchData();
}, []); 

