import  React from 'react';
import {BrowserRouter} from 'react-router-dom';
import "./css/main.css"

import './css/bootstrap.min.css'    
import './css/all.min.css'  
import './css/animate.css'  
import './css/magnific-popup.css'  
import './css/owl.min.css'
import './css/flaticon.css'  
import './css/odometer.css'

import  Carousel from "./pages/components/testComm/carousel"
/* import { SiteHeader } from './pages/components/header'; */
/* import { SiteFooter } from './pages/components/footer'; */


const Post_Component = ({posts }) =>{

  //const history = useHistory()
 // console.log("proverka0 ",posts)
  JSON.stringify(posts);
 // console.log("proverka1 ",posts )
  return(
      <>
            {
                  posts.map((e,i)=>   <div  key={i} class="col-lg-10" style={{background: "grey"}}>
                  <div class="post-item">
                      <div class="post-thumb">
                          <a href="blog-single.html">
                              <img src={'https://cdn.lk-ft.ru'+e.Post_image.url} alt="blog"/>
                          </a>
                      </div>
                      <div class="post-content">
                          <div class="post-date">
                              <a href="#0"><i class="flaticon-clock"></i>{e.Post_Date}</a>
                          </div>
                          <ul class="post-tags">
                              <li>
                                  <a href="#0">Видео</a>
                              </li>
                              <li>
                                  <a href="#0">Invest Ment</a>
                              </li>
                              <li>
                                  <a href="#0">Фильм</a>
                              </li>
                          </ul>
                          <h4 class="title">
                              <a href="/blog_single">{e.Post_Title}</a>
                          </h4>
                          <p>{e.Post_teaser}</p>
                          <a href="#single-post" class="custom-button"  >Подробности</a>
                      </div>
                  </div>
              </div> 
                      )
              }

      </>
  )
}  

const Pagination = ({postPerPage, totalPost , paginate}) => {

  const pageNumbers = [];

  //console.log("leng" , totalPost)

  for(let i = 1; i <=Math.ceil(totalPost / postPerPage); i++){
      pageNumbers.push(i);

  }

  return(
      <nav>
          <ul class="pagination mt-4 ">

              {pageNumbers.map( number =>

               <li key={number}  >
                   <a onClick={() => paginate(number)} href="#about" >{number}</a>
               </li>

               )}

          </ul>
      </nav>
  )
}

function Pagination_Post(){


  const [currentPage, setCurrentPage] = useState(1);
  const [postPerPage] = useState(2);

  const [posts, setPosts] = React.useState([]);
const [isLoading, setIsLoading] = React.useState(false);
const [isError, setIsError] = React.useState(false);
/* const fetchData = () => {
  fetch('https://cdn.lk-ft.ru/posts')
    .then((response) => response.json())
    .then((data) => {
      setIsLoading(false);
      setPosts(data);
    })
    .catch((error) => {
      setIsLoading(false);
      setIsError(true);
      console.log(error);
    });
};
React.useEffect(() => {
  fetchData();
}, []); */

React.useEffect(()=>{
    const fetchPosts = async () =>{
        setIsLoading(true);
        const res = await axios.get('https://cdn.lk-ft.ru/posts');
        setPosts(res.data);
        setIsLoading(false);
    }
})
//get current post
const  indexOfLastPost = currentPage * postPerPage;
const  indexOfFirstPost = indexOfLastPost - postPerPage;
const  currentPosts = posts.slice(indexOfFirstPost,indexOfLastPost);






// Change Page
const paginate = (pageNumber) => setCurrentPage(pageNumber);

      return(
          <>

             <Post_Component posts={currentPosts}  />
             <Pagination postPerPage={postPerPage} totalPost={posts.length} paginate={paginate} />

      </>
      )

} 



function App() {







      return(
          <>

             <Pagination_Post  />
      </>
      )

}



