import React,{useState} from "react";

export const Tournament = () =>{
    const [show,setShow] = useState(false);
    if(!show){
        return(
            <section>
                <div class="container">
               
                    <h1 class="title" onClick={()=>setShow(true)}>Достижения</h1>
               
                </div>
            </section>
        )
    }else{
        return(
            <section>
                <div  class="container">
                <div class="container">
               
                    <h1  class="title" onClick={()=>setShow(false)}>Достижения</h1>
                    <div class="container">
                    <div>
                           Секция в разработке
                    </div>
                    </div>
                
                </div>
                </div>
            </section>
        )

    }
    
}
