import React from "react";



export const SectionBlog = () =>{

    const [player, setPlayer] = React.useState([]);
    const fetchDataPlayer = () => {
        fetch('https://cdn.lk-ft.ru/footballers')
        .then((response) => response.json())
        .then((data) => {
            setPlayer(data);
        })
    };
    React.useEffect(() => {
        fetchDataPlayer();
    }, []);
    var author_icon;  

  const [posts, setPosts] = React.useState([]);
  const fetchData = () => {
    fetch('https://cdn.lk-ft.ru/posts')
      .then((response) => response.json())
      .then((data) => {
        setPosts(data);
      })
  };
  React.useEffect(() => {
    fetchData();
  }, []);
 

    var post_arr = [];
    posts.map((e,i) => {
        post_arr.push(e);
    })

    player.map((e,i) => {if(e.lastname == post_arr.Post_author_lastname && e.firstname == post_arr.Post_author ) author_icon = e.avatar})


    return(
        <>
                <section class="blog-section padding-top">
                    <div class="container">
                        <div class="section-header-2">
                            <h2 class="title">
                                <div class="stroke-text">Последние новсти</div>
                                <span class="text-theme">Новости</span>
                            </h2>
                        </div>
                        <div class="">
                        { post_arr.map((e,i) => <>
                            <div class="blog-item-2 mt-3">
                                <div class="blog-body">
                                    <h5 class="title"><a href="#0">{e.Post_Title}</a></h5>
                                    <p class="line-limit-3">{e.Post_teaser}</p>
                                    <a href="#0" class="read-more"><span>Подробнее</span></a>
                                </div>
                                <div class="blog-footer">
                                    <div class="author">
                                        <div class="thumb">
                                            <a href="#0" class="title"><img src={"https://cdn.lk-ft.ru"+author_icon?.url} alt="tweet"/></a>
                                        </div>
                                        <div class="content">
                                            <a href="#0" class="title">{e.Post_author} {e.Post_author_lastname}</a>
                                        </div>
                                    </div>
                                    <a href="#0"><i class="flaticon-calendar"></i>{e.Post_Date}</a>
                                </div>
                            </div>
                        </>
                        )}
                        </div>
                    </div>
                </section>
        </>
    )
}
