import React from "react";



export const SectionBlog = () =>{

    const [posts, setPosts] = React.useState([]);
  const [isLoading, setIsLoading] = React.useState(false);
  const [isError, setIsError] = React.useState(false);
  const fetchData = () => {
    fetch('https://cdn.lk-ft.ru/posts')
      .then((response) => response.json())
      .then((data) => {
        setIsLoading(false);
        setPosts(data);
      })
      .catch((error) => {
        setIsLoading(false);
        setIsError(true);
        console.log(error);
      });
  };
  React.useEffect(() => {
    fetchData();
  }, []);
  if (isLoading) {
      return <div>Загрузка...</div>;
    }

    var post_arr = [];
    posts.map((e,i) => {
        post_arr.push(e);
    })


    return(
        <>
                <section class="blog-section padding-top">
                    <div class="container">
                        <div class="section-header-2">
                            <h2 class="title">
                                <div class="stroke-text">Последние новсти</div>
                                <span class="text-theme">Новости</span>
                            </h2>
                        </div>
                        <div class="">
                        { post_arr.map((e,i) => <>
                            <div class="blog-item-2 mt-3">
                                <div class="blog-body">
                                    <h5 class="title"><a href="#0">{e.Post_Title}</a></h5>
                                    <p class="line-limit-3">{e.Post_teaser}</p>
                                    <a href="#0" class="read-more"><span>Подробнее</span></a>
                                </div>
                                <div class="blog-footer">
                                    <div class="author">
                                        <div class="thumb">
                                            <a href="#0" class="title"><img src="./images/tweet/tweet1.jpg" alt="tweet"/></a>
                                        </div>
                                        <div class="content">
                                            <a href="#0" class="title">{e.Post_author} {e.Post_author_lastname}</a>
                                        </div>
                                    </div>
                                    <a href="#0"><i class="flaticon-calendar"></i>{e.Post_Date}</a>
                                </div>
                            </div>
                        </>
                        )}
                        </div>
                    </div>
                </section>
        </>
    )
}
